import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Button } from "./Button";
import "./NavBar.css";
import "./Image.css";

const NavBar = () => {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  window.addEventListener("resize", showButton);

  return (
    <>
      <nav className="navbar">
        <div className="navbar-container">
          <Link to="/" className="navbar-logo">
            Min Goo Choi
            <img
              className="photo"
              src="/images/goldenbear.jpg"
              alt="/images/goldenbear.jpg"
            />
          </Link>
        </div>
        <p className="intro-text">
          "{"\n"} Currently a Student in University of California, Berkeley"
        </p>
      </nav>
      <nav className="navbar-container">
        <div>
          <div className="menu-icon" onClick={handleClick}>
            <i className={click ? "fas fa-times" : "fas fa-bars"} />
          </div>
          <ul className={click ? "nav-menu active" : "nav-menu"}>
            <li className="nav-item">
              <Link to="/" className="nav-links" onClick={closeMobileMenu}>
                About
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/blog" className="nav-links" onClick={closeMobileMenu}>
                Blog
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/photo" className="nav-links" onClick={closeMobileMenu}>
                Photo
              </Link>
            </li>
          </ul>
        </div>
      </nav>
      <img
        className="photo-sample"
        src="/images/sample.jpg"
        alt="/images/sample.jpg"
      />
    </>
  );
};

export default NavBar;
